// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import '@account-abstraction/contracts/core/BasePaymaster.sol';
import '@account-abstraction/contracts/core/UserOperationLib.sol';
import '@account-abstraction/contracts/core/Helpers.sol';
import '@openzeppelin/contracts/utils/cryptography/ECDSA.sol';
import '@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol';

/**
 * @title BelrosePaymaster
 * @notice A Verifying Paymaster that sponsors gas for users based on backend signatures.
 * Verified against TypeScript/Viem implementation for ERC-4337 v0.7 compatibility.
 */
contract BelrosePaymaster is BasePaymaster {
  using ECDSA for bytes32;
  using MessageHashUtils for bytes32;
  using UserOperationLib for PackedUserOperation;

  // ============ State Variables ============

  address public verifyingSigner;
  uint256 public maxCostPerUserOp;

  // ============ Events ============

  event SignerUpdated(address indexed oldSigner, address indexed newSigner);
  event MaxCostUpdated(uint256 oldMaxCost, uint256 newMaxCost);
  event GasSponsored(address indexed sender, uint256 maxCost);

  // ============ Errors ============

  error InvalidSignerAddress();

  // ============ Constructor ============

  constructor(
    IEntryPoint _entryPoint,
    address _verifyingSigner,
    uint256 _maxCostPerUserOp
  ) BasePaymaster(_entryPoint) {
    if (_verifyingSigner == address(0)) revert InvalidSignerAddress();
    verifyingSigner = _verifyingSigner;
    maxCostPerUserOp = _maxCostPerUserOp;
  }

  // ============ Core Paymaster Logic ============

  /**
   * @notice Validates the paymaster's participation in a UserOperation.
   * @dev Note: We calculate our own hash internally to ensure signature integrity.
   */
  function _validatePaymasterUserOp(
    PackedUserOperation calldata userOp,
    bytes32 /*userOpHash*/,
    uint256 maxCost
  ) internal view override returns (bytes memory context, uint256 validationData) {
    // 1. Safety Limit Check
    if (maxCost > maxCostPerUserOp) {
      return ('', _packValidationData(true, 0, 0));
    }

    // 2. Extract Custom Data (Starts at index 52)
    // Byte 1-52 consists of: Address (20b), Paymaster Verification Gas Limit (16b), Paymaster Post-Op Gas Limit (16b)
    // Remainder is customData [validUntil: 6b][validAfter: 6b][signature: 65b] = 77 bytes
    bytes calldata customData = userOp.paymasterAndData[52:];
    if (customData.length < 77) {
      return ('', _packValidationData(true, 0, 0));
    }

    uint48 validUntil = uint48(bytes6(customData[0:6]));
    uint48 validAfter = uint48(bytes6(customData[6:12]));
    bytes calldata signature = customData[12:];

    // 3. Compute Hashing (The logic verified in our tests)
    bytes32 hashToSign = getHashWithZeroSignature(userOp, validUntil, validAfter);

    // 4. Verify EIP-191 Signature
    // toEthSignedMessageHash adds "\x19Ethereum Signed Message:\n32" prefix
    address recovered = hashToSign.toEthSignedMessageHash().recover(signature);

    bool signatureFailed = (recovered != verifyingSigner);
    return ('', _packValidationData(signatureFailed, validUntil, validAfter));
  }

  /**
   * @notice Replicates the exact hashing flow verified in HashTester.test.ts
   */
  function getHashWithZeroSignature(
    PackedUserOperation calldata userOp,
    uint48 validUntil,
    uint48 validAfter
  ) public view returns (bytes32) {
    // Build paymasterData with zero signature placeholder
    bytes memory paymasterDataWithZeroSig = abi.encodePacked(validUntil, validAfter, new bytes(65));

    // Compute the "Inner" UserOp Hash
    bytes32 userOpHashWithZeroSig = _computeUserOpHash(userOp, paymasterDataWithZeroSig);

    // Wrap with ChainID and Paymaster-specific context
    return
      keccak256(
        abi.encode(userOpHashWithZeroSig, block.chainid, address(this), validUntil, validAfter)
      );
  }

  /**
   * @dev Internal helper to compute UserOp hash matching EntryPoint v0.7 logic.
   * Scoped variables prevent "Stack Too Deep" errors.
   */
  function _computeUserOpHash(
    PackedUserOperation calldata userOp,
    bytes memory paymasterDataOverride
  ) internal view returns (bytes32) {
    bytes32 paymasterAndDataHash;
    {
      // Extract the gas limits from paymasterAndData bytes
      bytes16 pmVerif = bytes16(userOp.paymasterAndData[20:36]);
      bytes16 pmPost = bytes16(userOp.paymasterAndData[36:52]);
      paymasterAndDataHash = keccak256(
        abi.encodePacked(address(this), pmVerif, pmPost, paymasterDataOverride)
      );
    }

    bytes32 innerHash = keccak256(
      abi.encode(
        userOp.sender,
        userOp.nonce,
        keccak256(userOp.initCode),
        keccak256(userOp.callData),
        userOp.accountGasLimits,
        userOp.preVerificationGas,
        userOp.gasFees,
        paymasterAndDataHash
      )
    );

    return keccak256(abi.encode(innerHash, address(entryPoint), block.chainid));
  }

  function _postOp(PostOpMode, bytes calldata, uint256, uint256) internal pure override {}

  // ============ Admin Functions ============

  function updateSigner(address _newSigner) external onlyOwner {
    if (_newSigner == address(0)) revert InvalidSignerAddress();
    verifyingSigner = _newSigner;
    emit SignerUpdated(verifyingSigner, _newSigner);
  }

  function setMaxCostPerUserOp(uint256 _newMaxCost) external onlyOwner {
    maxCostPerUserOp = _newMaxCost;
    emit MaxCostUpdated(maxCostPerUserOp, _newMaxCost);
  }
}

contracts/HealthRecordCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import './MemberRoleManager.sol';

/**
 * @title HealthRecordCore
 * @dev Handles access permissions, record anchoring, and record reviews
 * References MemberRoleManager for membership and role checks
 */
contract HealthRecordCore {
  // ===============================================================
  // CONTRACT SETUP
  // ===============================================================

  MemberRoleManagerInterface public memberRoleManager;
  address public admin;

  event AdminTransferred(address indexed oldAdmin, address indexed newAdmin, uint256 timestamp);

  modifier onlyAdmin() {
    require(msg.sender == admin, 'Only admin');
    _;
  }

  modifier onlyActiveMember() {
    require(memberRoleManager.isActiveMember(msg.sender), 'Not an active member');
    _;
  }

  modifier onlyVerifiedMember() {
    require(memberRoleManager.isVerifiedMember(msg.sender), 'Not a verified member');
    _;
  }

  modifier onlyRecordParticipant(string memory recordId) {
    require(memberRoleManager.hasActiveRole(recordId, msg.sender), 'No access to this record');
    _;
  }

  constructor(address _memberRoleManager) {
    require(_memberRoleManager != address(0), 'Invalid MemberRoleManager address');
    memberRoleManager = MemberRoleManagerInterface(_memberRoleManager);
    admin = msg.sender;
  }

  function transferAdmin(address newAdmin) external onlyAdmin {
    require(newAdmin != address(0), 'Invalid address');
    emit AdminTransferred(admin, newAdmin, block.timestamp);
    admin = newAdmin;
  }

  /**
   * @notice Update the MemberRoleManager reference (in case of upgrade)
   */
  function setMemberRoleManager(address _memberRoleManager) external onlyAdmin {
    require(_memberRoleManager != address(0), 'Invalid address');
    memberRoleManager = MemberRoleManagerInterface(_memberRoleManager);
  }

  // ===============================================================
  // ACCESS PERMISSIONS
  // ===============================================================

  // =================== ACCESS PERMISSIONS - EVENTS ===================

  event AccessGranted(
    string indexed permissionHash,
    address indexed sharer,
    address indexed receiver,
    string recordId,
    uint256 timestamp
  );

  event AccessRevoked(
    string indexed permissionHash,
    address indexed sharer,
    address indexed receiver,
    string recordId,
    uint256 timestamp
  );

  // =================== ACCESS PERMISSIONS - STRUCTURE ===================

  struct AccessPermission {
    string permissionHash;
    address sharer;
    address receiver;
    string recordId;
    uint256 grantedAt;
    uint256 revokedAt;
    bool isActive;
    bool exists;
  }

  // =================== ACCESS PERMISSIONS - STORAGE ===================

  mapping(string => AccessPermission) public accessPermissions;
  mapping(address => string[]) public permissionsBySharer;
  mapping(address => string[]) public permissionsByReceiver;
  uint256 public totalPermissions;

  // =================== ACCESS PERMISSIONS - FUNCTIONS ===================

  /**
   * @notice Grant access to a record
   * @param permissionHash Hash of the permission details (from frontend)
   * @param recordId The internal record ID
   * @param receiver Address of the person receiving access
   */
  function grantAccess(
    string memory permissionHash,
    string memory recordId,
    address receiver
  ) external onlyActiveMember onlyRecordParticipant(recordId) {
    require(bytes(permissionHash).length > 0, 'Permission hash cannot be empty');
    require(bytes(recordId).length > 0, 'Record ID cannot be empty');
    require(receiver != address(0), 'Receiver address cannot be zero');
    require(receiver != msg.sender, 'Cannot share with yourself');
    require(!accessPermissions[permissionHash].exists, 'Permission already exists');

    accessPermissions[permissionHash] = AccessPermission({
      permissionHash: permissionHash,
      sharer: msg.sender,
      receiver: receiver,
      recordId: recordId,
      grantedAt: block.timestamp,
      revokedAt: 0,
      isActive: true,
      exists: true
    });

    permissionsBySharer[msg.sender].push(permissionHash);
    permissionsByReceiver[receiver].push(permissionHash);
    totalPermissions++;

    emit AccessGranted(permissionHash, msg.sender, receiver, recordId, block.timestamp);
  }

  /**
   * @notice Revoke access to a record
   * @param permissionHash The permission hash to revoke
   */
  function revokeAccess(string memory permissionHash) external {
    require(accessPermissions[permissionHash].exists, 'Permission does not exist');
    require(accessPermissions[permissionHash].isActive, 'Already revoked');
    require(accessPermissions[permissionHash].sharer == msg.sender, 'Only the sharer can revoke');

    accessPermissions[permissionHash].isActive = false;
    accessPermissions[permissionHash].revokedAt = block.timestamp;

    emit AccessRevoked(
      permissionHash,
      msg.sender,
      accessPermissions[permissionHash].receiver,
      accessPermissions[permissionHash].recordId,
      block.timestamp
    );
  }

  /**
   * @notice Check if access is currently active
   */
  function checkAccess(
    string memory permissionHash
  )
    external
    view
    returns (
      bool isActive,
      address sharer,
      address receiver,
      string memory recordId,
      uint256 grantedAt,
      uint256 revokedAt
    )
  {
    AccessPermission memory permission = accessPermissions[permissionHash];
    return (
      permission.isActive,
      permission.sharer,
      permission.receiver,
      permission.recordId,
      permission.grantedAt,
      permission.revokedAt
    );
  }

  /**
   * @notice Get all permissions granted by a sharer
   */
  function getPermissionsBySharer(address sharer) external view returns (string[] memory) {
    return permissionsBySharer[sharer];
  }

  /**
   * @notice Get all permissions for a receiver
   */
  function getPermissionsByReceiver(address receiver) external view returns (string[] memory) {
    return permissionsByReceiver[receiver];
  }

  /**
   * @notice Get total number of permissions
   */
  function getTotalPermissions() external view returns (uint256) {
    return totalPermissions;
  }

  /**
   * @notice Get permission statistics for a sharer
   */
  function getSharerStats(address sharer) external view returns (uint256 total, uint256 active) {
    string[] memory sharerPermissions = permissionsBySharer[sharer];
    total = sharerPermissions.length;
    active = 0;

    for (uint256 i = 0; i < sharerPermissions.length; i++) {
      if (accessPermissions[sharerPermissions[i]].isActive) {
        active++;
      }
    }

    return (total, active);
  }

  // ===============================================================
  // RECORD ANCHORING
  // ===============================================================

  // =================== RECORD ANCHORING - EVENTS ===================

  event RecordAnchored(
    string indexed recordId,
    string indexed recordHash,
    address indexed subject,
    address createdBy,
    uint256 timestamp
  );

  // =================== RECORD ANCHORING - STRUCTURE ===================

  struct AnchoredRecord {
    string recordHash;
    string recordId;
    address subject; // Who this record is ABOUT (the patient)
    uint256 createdAt;
    address createdBy;
    bool exists;
  }

  // =================== RECORD ANCHORING - STORAGE ===================

  mapping(string => AnchoredRecord) public anchoredRecords;
  mapping(address => string[]) public recordsAboutSubject;
  mapping(string => string[]) public recordHashesByRecordId;
  uint256 public totalAnchoredRecords;

  // =================== RECORD ANCHORING - FUNCTIONS ===================

  /**
   * @notice Anchor a record hash with its metadata
   * @param recordHash SHA-256 hash of the record contents
   * @param recordId Your internal record ID
   * @param subject The patient/person this record is about
   */
  function anchorRecord(
    string memory recordHash,
    string memory recordId,
    address subject
  ) external onlyVerifiedMember onlyRecordParticipant(recordId) {
    require(bytes(recordHash).length > 0, 'Hash cannot be empty');
    require(bytes(recordId).length > 0, 'Record ID cannot be empty');
    require(subject != address(0), 'Subject cannot be zero address');
    require(!anchoredRecords[recordHash].exists, 'Hash already anchored');

    anchoredRecords[recordHash] = AnchoredRecord({
      recordHash: recordHash,
      recordId: recordId,
      subject: subject,
      createdAt: block.timestamp,
      createdBy: msg.sender,
      exists: true
    });

    recordsAboutSubject[subject].push(recordHash);
    recordHashesByRecordId[recordId].push(recordHash);
    totalAnchoredRecords++;

    emit RecordAnchored(recordHash, recordId, subject, msg.sender, block.timestamp);
  }

  /**
   * @notice Get record metadata
   */
  function getAnchoredRecord(
    string memory recordHash
  )
    external
    view
    returns (
      string memory recordId,
      address subject,
      uint256 createdAt,
      address createdBy,
      bool exists
    )
  {
    AnchoredRecord memory record = anchoredRecords[recordHash];
    return (record.recordId, record.subject, record.createdAt, record.createdBy, record.exists);
  }

  /**
   * @notice Get the subject (patient) of a record
   */
  function getRecordSubject(string memory recordHash) external view returns (address) {
    require(anchoredRecords[recordHash].exists, 'Record does not exist');
    return anchoredRecords[recordHash].subject;
  }

  /**
   * @notice Get all records where an address is the subject
   */
  function getRecordsAboutSubject(address subject) external view returns (string[] memory) {
    return recordsAboutSubject[subject];
  }

  /**
   * @notice Get all versions of a record
   */
  function getHashesByRecordId(string memory recordId) external view returns (string[] memory) {
    return recordHashesByRecordId[recordId];
  }

  /**
   * @notice Check if an address is the subject of a record
   */
  function isSubject(string memory recordHash, address user) external view returns (bool) {
    return anchoredRecords[recordHash].subject == user;
  }

  // ===============================================================
  // RECORD REVIEWS
  // ===============================================================

  // =================== RECORD REVIEWS - EVENTS ===================

  event RecordReviewed(
    string indexed recordId,
    string indexed recordHash,
    address indexed reviewer,
    ReviewType reviewType,
    DisputeSeverity severity,
    DisputeCulpability culpability,
    uint256 timestamp
  );

  event ReviewRetracted(
    string indexed recordHash,
    address indexed reviewer,
    ReviewType reviewType,
    uint256 timestamp
  );

  event DisputeReaction(
    string indexed recordHash,
    address indexed reactor,
    address indexed reviewer,
    bool supportsDispute,
    uint256 timestamp
  );

  event DisputeModification(
    string indexed recordHash,
    address indexed reviewer,
    DisputeSeverity oldSeverity,
    DisputeSeverity newSeverity,
    DisputeCulpability oldCulpability,
    DisputeCulpability newCulpability,
    uint256 timestamp
  );

  // =================== RECORD REVIEWS - ENUMS ===================

  enum ReviewType {
    Verification,
    Dispute
  }

  enum DisputeSeverity {
    None,
    Negligible,
    Moderate,
    Major
  }

  enum DisputeCulpability {
    None,
    NoFault,
    Systemic,
    Preventable,
    Reckless,
    Intentional
  }

  // =================== RECORD REVIEWS - STRUCTURES ===================

  struct Review {
    address reviewer;
    ReviewType reviewType;
    DisputeSeverity severity;
    DisputeCulpability culpability;
    uint256 timestamp;
    string notes;
    bool isActive;
  }

  struct Reaction {
    address reactor;
    bool supportsDispute;
    uint256 timestamp;
  }

  // =================== RECORD REVIEWS - STORAGE ===================

  mapping(string => Review[]) public recordReviews;
  mapping(string => mapping(address => bool)) public hasReviewed;
  mapping(string => mapping(address => uint256)) public userReviewIndex;
  mapping(string => mapping(address => Reaction[])) public disputeReactions;
  mapping(string => mapping(address => mapping(address => bool))) public hasReactedToDispute;
  mapping(address => string[]) public verificationsByUser;
  mapping(address => string[]) public disputesByUser;
  uint256 public totalReviews;

  // =================== RECORD REVIEWS - INTERNAL FUNCTIONS ===================

  /**
   * @dev Internal function to handle both verifications and disputes
   */
  function _reviewRecord(
    string memory recordHash,
    string memory notes,
    ReviewType reviewType,
    DisputeSeverity severity,
    DisputeCulpability culpability
  ) internal {
    require(anchoredRecords[recordHash].exists, 'Record not anchored');
    string memory recordId = anchoredRecords[recordHash].recordId;

    // Must have a role on this record
    require(memberRoleManager.hasActiveRole(recordId, msg.sender), 'No access to this record');

    // One review per user per hash
    require(!hasReviewed[recordHash][msg.sender], 'Already reviewed this record');

    uint256 newIndex = recordReviews[recordHash].length;

    recordReviews[recordHash].push(
      Review({
        reviewer: msg.sender,
        reviewType: reviewType,
        severity: severity,
        culpability: culpability,
        timestamp: block.timestamp,
        notes: notes,
        isActive: true
      })
    );

    hasReviewed[recordHash][msg.sender] = true;
    userReviewIndex[recordHash][msg.sender] = newIndex;
    totalReviews++;

    emit RecordReviewed(
      recordHash,
      recordId,
      msg.sender,
      reviewType,
      severity,
      culpability,
      block.timestamp
    );
  }

  // =================== RECORD REVIEWS - EXTERNAL FUNCTIONS ===================

  /**
   * @notice Verify a record (attest that it's accurate)
   * @param recordHash The hash being verified
   * @param notes Optional hash of detailed notes (stored off-chain)
   */
  function verifyRecord(string memory recordHash, string memory notes) external onlyVerifiedMember {
    _reviewRecord(
      recordHash,
      notes,
      ReviewType.Verification,
      DisputeSeverity.None,
      DisputeCulpability.None
    );
    verificationsByUser[msg.sender].push(recordHash);
  }

  /**
   * @notice Dispute a record (claim it's inaccurate/tampered)
   * @param recordHash The hash being disputed
   * @param severity 1=Negligible, 2=Moderate, 3=Major
   * @param culpability 1=NoFault, 2=Systemic, 3=Preventable, 4=Reckless, 5=Intentional
   * @param notes Optional hash of detailed reasoning (stored off-chain)
   */
  function disputeRecord(
    string memory recordHash,
    uint8 severity,
    uint8 culpability,
    string memory notes
  ) external onlyVerifiedMember {
    require(severity >= 1 && severity <= 3, 'Severity must be 1-3');
    require(culpability >= 1 && culpability <= 5, 'Culpability must be 1-5');

    _reviewRecord(
      recordHash,
      notes,
      ReviewType.Dispute,
      DisputeSeverity(severity),
      DisputeCulpability(culpability)
    );
    disputesByUser[msg.sender].push(recordHash);
  }

  /**
   * @notice Retract your review (verification or dispute)
   * @param recordHash The hash you reviewed
   */
  function retractReview(string memory recordHash) external {
    require(hasReviewed[recordHash][msg.sender], 'No review to retract');

    uint256 index = userReviewIndex[recordHash][msg.sender];
    Review storage review = recordReviews[recordHash][index];

    require(review.isActive, 'Already retracted');

    review.isActive = false;
    hasReviewed[recordHash][msg.sender] = false;

    emit ReviewRetracted(recordHash, msg.sender, review.reviewType, block.timestamp);
  }

  /**
   * @notice Modify severity and culpability on an active dispute
   * @param recordHash The hash of the record with the dispute
   * @param newSeverity The new severity score (1-3)
   * @param newCulpability The new culpability score (1-5)
   */
  function modifyDispute(
    string memory recordHash,
    uint8 newSeverity,
    uint8 newCulpability
  ) external {
    require(anchoredRecords[recordHash].exists, 'Record not anchored');
    require(hasReviewed[recordHash][msg.sender], 'User has no review on this record');
    require(newSeverity >= 1 && newSeverity <= 3, 'Severity must be 1-3');
    require(newCulpability >= 1 && newCulpability <= 5, 'Culpability must be 1-5');

    uint256 index = userReviewIndex[recordHash][msg.sender];
    Review storage review = recordReviews[recordHash][index];

    require(review.isActive, 'Dispute has been retracted');
    require(review.reviewType == ReviewType.Dispute, 'Review is not a dispute');

    DisputeSeverity oldSeverity = review.severity;
    DisputeCulpability oldCulpability = review.culpability;

    review.severity = DisputeSeverity(newSeverity);
    review.culpability = DisputeCulpability(newCulpability);

    emit DisputeModification(
      recordHash,
      msg.sender,
      oldSeverity,
      review.severity,
      oldCulpability,
      review.culpability,
      block.timestamp
    );
  }

  /**
   * @notice React to a dispute (thumbs up or thumbs down)
   * @param recordHash The record hash with the dispute
   * @param disputer The address who created the dispute
   * @param supportsDispute True = agree with dispute, False = disagree
   */
  function reactToDispute(
    string memory recordHash,
    address disputer,
    bool supportsDispute
  ) external onlyActiveMember {
    require(anchoredRecords[recordHash].exists, 'Record not anchored');
    require(hasReviewed[recordHash][disputer], 'No dispute from this address');

    uint256 disputeIndex = userReviewIndex[recordHash][disputer];
    Review memory review = recordReviews[recordHash][disputeIndex];
    require(review.reviewType == ReviewType.Dispute, 'Not a dispute');
    require(review.isActive, 'Dispute has been retracted');

    string memory recordId = anchoredRecords[recordHash].recordId;
    require(memberRoleManager.hasActiveRole(recordId, msg.sender), 'No access to this record');

    require(msg.sender != disputer, 'Cannot react to your own dispute');
    require(
      !hasReactedToDispute[recordHash][disputer][msg.sender],
      'Already reacted to this dispute'
    );

    disputeReactions[recordHash][disputer].push(
      Reaction({
        reactor: msg.sender,
        supportsDispute: supportsDispute,
        timestamp: block.timestamp
      })
    );

    hasReactedToDispute[recordHash][disputer][msg.sender] = true;

    emit DisputeReaction(recordHash, msg.sender, disputer, supportsDispute, block.timestamp);
  }

  // =================== RECORD REVIEWS - VIEW FUNCTIONS ===================

  /**
   * @notice Get all reviews for a record hash
   */
  function getReviews(string memory recordHash) external view returns (Review[] memory) {
    return recordReviews[recordHash];
  }

  /**
   * @notice Get review counts for a record hash
   */
  function getReviewStats(
    string memory recordHash
  )
    external
    view
    returns (uint256 activeVerifications, uint256 activeDisputes, uint256 retractedCount)
  {
    Review[] memory reviews = recordReviews[recordHash];

    for (uint256 i = 0; i < reviews.length; i++) {
      if (reviews[i].isActive) {
        if (reviews[i].reviewType == ReviewType.Verification) {
          activeVerifications++;
        } else {
          activeDisputes++;
        }
      } else {
        retractedCount++;
      }
    }

    return (activeVerifications, activeDisputes, retractedCount);
  }

  /**
   * @notice Get a user's review on a record hash
   */
  function getUserReview(
    string memory recordHash,
    address user
  )
    external
    view
    returns (
      bool exists,
      ReviewType reviewType,
      uint256 timestamp,
      string memory notes,
      bool isActive
    )
  {
    if (!hasReviewed[recordHash][user]) {
      return (false, ReviewType.Verification, 0, '', false);
    }

    uint256 index = userReviewIndex[recordHash][user];
    Review memory review = recordReviews[recordHash][index];

    return (true, review.reviewType, review.timestamp, review.notes, review.isActive);
  }

  /**
   * @notice Get reactions to a specific dispute
   */
  function getDisputeReactions(
    string memory recordHash,
    address disputer
  ) external view returns (Reaction[] memory) {
    return disputeReactions[recordHash][disputer];
  }

  /**
   * @notice Get reaction counts for a dispute
   */
  function getDisputeReactionStats(
    string memory recordHash,
    address disputer
  ) external view returns (uint256 supportsCount, uint256 opposesCount) {
    Reaction[] memory reactions = disputeReactions[recordHash][disputer];

    for (uint256 i = 0; i < reactions.length; i++) {
      if (reactions[i].supportsDispute) {
        supportsCount++;
      } else {
        opposesCount++;
      }
    }

    return (supportsCount, opposesCount);
  }

  /**
   * @notice Get user's review history
   */
  function getUserReviewHistory(
    address user
  ) external view returns (uint256 totalVerifications, uint256 totalDisputes) {
    return (verificationsByUser[user].length, disputesByUser[user].length);
  }

  /**
   * @notice Get all record hashes a user has verified
   */
  function getUserVerifications(address user) external view returns (string[] memory) {
    return verificationsByUser[user];
  }

  /**
   * @notice Get all record hashes a user has disputed
   */
  function getUserDisputes(address user) external view returns (string[] memory) {
    return disputesByUser[user];
  }

  /**
   * @notice Get total counts
   */
  function getTotalStats()
    external
    view
    returns (uint256 totalAnchored, uint256 totalReviewCount, uint256 totalPermissionCount)
  {
    return (totalAnchoredRecords, totalReviews, totalPermissions);
  }
}

contracts/MemberRoleManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title MemberRoleManagerInterface
 * @dev Interface for HealthRecordCore to reference
 */
interface MemberRoleManagerInterface {
  function isActiveMember(address wallet) external view returns (bool);

  function isVerifiedMember(address wallet) external view returns (bool);

  function hasActiveRole(string memory recordId, address wallet) external view returns (bool);

  function hasRole(
    string memory recordId,
    address wallet,
    string memory role
  ) external view returns (bool);

  function isOwnerOrAdmin(string memory recordId, address wallet) external view returns (bool);
}

/**
 * @title MemberRoleManager
 * @dev Manages multi-wallet member registration and role-based access control.
 *
 * KEY ARCHITECTURE:
 * - Members are registered by WALLET ADDRESS
 * - Each wallet is linked to an IDENTITY (userIdHash)
 * - One identity can have MULTIPLE wallets (EOA, Smart Account, etc.)
 * - Roles are assigned to IDENTITIES, not wallets
 * - Any wallet linked to an identity can exercise that identity's roles
 */
contract MemberRoleManager is MemberRoleManagerInterface {
  // ===============================================================
  // ADMIN MANAGEMENT
  // ===============================================================
  address public admin;

  event AdminTransferred(address indexed oldAdmin, address indexed newAdmin, uint256 timestamp);

  modifier onlyAdmin() {
    require(msg.sender == admin, 'Only admin');
    _;
  }

  modifier onlyActiveMember() {
    require(_isActiveMember(msg.sender), 'Not an active member');
    _;
  }

  modifier onlyVerifiedMember() {
    require(_isVerifiedMember(msg.sender), 'Not a verified member');
    _;
  }

  constructor() {
    admin = msg.sender;
  }

  function transferAdmin(address newAdmin) external onlyAdmin {
    require(newAdmin != address(0), 'Invalid address');
    emit AdminTransferred(admin, newAdmin, block.timestamp);
    admin = newAdmin;
  }

  // ===============================================================
  // MEMBER REGISTRY
  // ===============================================================

  // =================== MEMBER REGISTRY - ENUMS ===================

  enum MemberStatus {
    NotRegistered, // 0 - Default/uninitialized
    Inactive, // 1 - Cannot transact (banned/removed)
    Active, // 2 - Default for new members
    Verified // 3 - User has verified their identity and email
  }

  // =================== MEMBER REGISTRY - EVENTS ===================

  event MemberRegistered(address indexed wallet, bytes32 indexed userIdHash, uint256 timestamp);

  event WalletLinked(address indexed wallet, bytes32 indexed userIdHash, uint256 timestamp);

  event MemberStatusChanged(
    bytes32 indexed userIdHash,
    MemberStatus oldStatus,
    MemberStatus newStatus,
    address indexed changedBy,
    uint256 timestamp
  );

  // =================== MEMBER REGISTRY - STRUCTURE ===================

  // Wallet's Member info - maps a wallet to its identity (userIdHash)
  struct UserInfo {
    bytes32 userIdHash; // The identity this wallet belongs to
    bool isWalletActive; // Whether this wallet can be used
  }

  // =================== MEMBER REGISTRY - STORAGE ===================

  // Map address to UserIdHash and status (whether wallet is active or not)
  mapping(address => UserInfo) public wallets;

  // map userIdHash with memberStatus
  mapping(bytes32 => MemberStatus) public userStatus;

  // Identity (userIdHash) => list of wallet addresses
  mapping(bytes32 => address[]) public userWallets;

  // For enumeration
  bytes32[] public userList;
  uint256 public totalUsers;

  // =================== MEMBER REGISTRY - FUNCTIONS ===================

  /**
   * @notice Register a new wallet for an identity (can only be called by Admin)
   * @dev If this is the first wallet for this userIdHash, creates the identity
   * @param wallet The wallet address to register
   * @param userIdHash Hash of their off-chain user ID
   */
  function addMember(address wallet, bytes32 userIdHash) external onlyAdmin {
    require(wallet != address(0), 'Invalid wallet address');
    require(userIdHash != bytes32(0), 'Invalid user ID hash');
    require(wallets[wallet].userIdHash == bytes32(0), 'Wallet already registered');

    // Link wallet to user
    wallets[wallet] = UserInfo({ userIdHash: userIdHash, isWalletActive: true });

    // Add to user's's wallet list
    userWallets[userIdHash].push(wallet);

    // If new user, initialize active status
    if (userStatus[userIdHash] == MemberStatus.NotRegistered) {
      userStatus[userIdHash] = MemberStatus.Active;
      userList.push(userIdHash);
      totalUsers++;

      emit MemberRegistered(wallet, userIdHash, block.timestamp);
    } else {
      emit WalletLinked(wallet, userIdHash, block.timestamp);
    }
  }

  /**
   * @notice Change an identity's status (affects all their wallets)
   * @param userIdHash The identity to update
   * @param newStatus The new status
   */
  function setUserStatus(bytes32 userIdHash, MemberStatus newStatus) external onlyAdmin {
    require(userStatus[userIdHash] != MemberStatus.NotRegistered, 'User not registered');

    MemberStatus oldStatus = userStatus[userIdHash];
    require(oldStatus != newStatus, 'Already this status');

    userStatus[userIdHash] = newStatus;

    emit MemberStatusChanged(userIdHash, oldStatus, newStatus, msg.sender, block.timestamp);
  }

  /**
   * @notice Deactivate a specific wallet (not the whole user)
   * @param wallet The wallet to deactivate
   */
  function deactivateWallet(address wallet) external onlyAdmin {
    require(wallets[wallet].userIdHash != bytes32(0), 'Wallet not registered');
    require(wallets[wallet].isWalletActive, 'Wallet already inactive');

    wallets[wallet].isWalletActive = false;
  }

  /**
   * @notice Reactivate a specific wallet
   * @param wallet The wallet to reactivate
   */
  function reactivateWallet(address wallet) external onlyAdmin {
    require(wallets[wallet].userIdHash != bytes32(0), 'Wallet not registered');
    require(!wallets[wallet].isWalletActive, 'Wallet already active');

    wallets[wallet].isWalletActive = true;
  }

  // =================== MEMBER REGISTRY - VIEW FUNCTIONS ===================

  /**
   * @notice Check if wallet belongs to an active member
   */
  function isActiveMember(address wallet) external view override returns (bool) {
    return _isActiveMember(wallet);
  }

  function _isActiveMember(address wallet) internal view returns (bool) {
    bytes32 userIdHash = wallets[wallet].userIdHash;
    if (userIdHash == bytes32(0) || !wallets[wallet].isWalletActive) return false;

    MemberStatus status = userStatus[userIdHash];
    return status != MemberStatus.Inactive && status != MemberStatus.NotRegistered;
  }

  /**
   * @notice Check if wallet belongs to a verified member
   */
  function isVerifiedMember(address wallet) external view override returns (bool) {
    return _isVerifiedMember(wallet);
  }

  function _isVerifiedMember(address wallet) internal view returns (bool) {
    bytes32 userIdHash = wallets[wallet].userIdHash;
    if (userIdHash == bytes32(0) || !wallets[wallet].isWalletActive) return false;

    return userStatus[userIdHash] == MemberStatus.Verified;
  }

  /**
   * @notice Get the identity (userIdHash) for a wallet
   */
  function getUserForWallet(address wallet) external view returns (bytes32) {
    return wallets[wallet].userIdHash;
  }

  /**
   * @notice Get all wallets for an identity
   */
  function getWalletsForUser(bytes32 userIdHash) external view returns (address[] memory) {
    return userWallets[userIdHash];
  }

  /**
   * @notice Get user status (not registered, inactive, active, verified)
   */
  function getUserStatus(bytes32 userIdHash) external view returns (MemberStatus status) {
    return userStatus[userIdHash];
  }

  /**
   * @notice Get all Users
   */
  function getAllUsers() external view returns (bytes32[] memory) {
    return userList;
  }

  /**
   * @notice Get total number of identities
   */
  function getTotalUsers() external view returns (uint256) {
    return totalUsers;
  }

  // ===============================================================
  // ROLE MANAGEMENT
  // ===============================================================

  // =================== ROLE MANAGEMENT - EVENTS ===================

  event RoleGranted(
    string indexed recordId,
    bytes32 indexed targetIdHash,
    string role,
    bytes32 indexed userIdHash,
    uint256 timestamp
  );

  event RoleChanged(
    string indexed recordId,
    bytes32 indexed targetIdHash,
    string oldRole,
    string newRole,
    bytes32 indexed userIdHash,
    uint256 timestamp
  );

  event RoleRevoked(
    string indexed recordId,
    bytes32 indexed targetIdHash,
    string role,
    bytes32 indexed userIdHash,
    uint256 timestamp
  );

  event OwnershipVoluntarilyLeft(
    string indexed recordId,
    bytes32 indexed userIdHash,
    uint256 timestamp
  );

  // =================== ROLE MANAGEMENT - STRUCTURE ===================

  struct RecordRole {
    string role; // "owner", "administrator", "viewer"
    bool isActive;
  }

  // =================== ROLE MANAGEMENT - STORAGE ===================

  // hash of recordId and userIdHash => RecordRole
  mapping(bytes32 => RecordRole) public recordRoles;

  // recordId => list of identity hashes with each role type
  mapping(string => bytes32[]) public ownersByRecord;
  mapping(string => bytes32[]) public adminsByRecord;
  mapping(string => bytes32[]) public viewersByRecord;

  // userIdHash => list of recordIds where they have a role
  mapping(bytes32 => string[]) public recordsByUser;

  uint256 public totalRoles;

  // =================== ROLE MANAGEMENT - INTERNAL HELPERS ===================

  /**
   * @dev Get the role key for a (recordId, userIdHash) pair
   */
  function _getRoleKey(string memory recordId, bytes32 userIdHash) internal pure returns (bytes32) {
    return keccak256(abi.encodePacked(recordId, userIdHash));
  }

  /**
   * @dev Get caller's identity hash
   */
  function _getCallerIdHash() internal view returns (bytes32) {
    return wallets[msg.sender].userIdHash;
  }

  /**
   * @dev Makes sure the role is a valid role
   */
  function _isValidRole(string memory role) internal pure returns (bool) {
    bytes32 h = keccak256(bytes(role));
    return (h == keccak256(bytes('owner')) ||
      h == keccak256(bytes('administrator')) ||
      h == keccak256(bytes('viewer')));
  }

  function _grantRoleInternal(
    string memory recordId,
    bytes32 targetIdHash,
    string memory role,
    bytes32 userIdHash
  ) internal {
    bytes32 roleKey = _getRoleKey(recordId, targetIdHash);

    RecordRole storage currentEntry = recordRoles[roleKey];
    string memory oldRole = currentEntry.role;
    bool hasExistingEntry = bytes(oldRole).length > 0;
    bool isCurrentlyActive = currentEntry.isActive;

    recordRoles[roleKey] = RecordRole({ role: role, isActive: true });

    if (!isCurrentlyActive) {
      _addToRoleArray(recordId, targetIdHash, role);

      if (!hasExistingEntry) {
        recordsByUser[targetIdHash].push(recordId);
        totalRoles++;
      }
      emit RoleGranted(recordId, targetIdHash, role, userIdHash, block.timestamp);
    } else {
      if (keccak256(bytes(oldRole)) != keccak256(bytes(role))) {
        _removeFromRoleArray(recordId, targetIdHash, oldRole);
        _addToRoleArray(recordId, targetIdHash, role);
      }
    }
  }

  function _addToRoleArray(
    string memory recordId,
    bytes32 userIdHash,
    string memory role
  ) internal {
    bytes32 roleHash = keccak256(bytes(role));

    if (roleHash == keccak256(bytes('owner'))) {
      ownersByRecord[recordId].push(userIdHash);
    } else if (roleHash == keccak256(bytes('administrator'))) {
      adminsByRecord[recordId].push(userIdHash);
    } else if (roleHash == keccak256(bytes('viewer'))) {
      viewersByRecord[recordId].push(userIdHash);
    }
  }

  function _removeFromRoleArray(
    string memory recordId,
    bytes32 userIdHash,
    string memory role
  ) internal {
    bytes32 roleHash = keccak256(bytes(role));

    if (roleHash == keccak256(bytes('owner'))) {
      _removeFromBytes32Array(ownersByRecord[recordId], userIdHash);
    } else if (roleHash == keccak256(bytes('administrator'))) {
      _removeFromBytes32Array(adminsByRecord[recordId], userIdHash);
    } else if (roleHash == keccak256(bytes('viewer'))) {
      _removeFromBytes32Array(viewersByRecord[recordId], userIdHash);
    }
  }

  function _removeFromBytes32Array(bytes32[] storage array, bytes32 toRemove) internal {
    for (uint256 i = 0; i < array.length; i++) {
      if (array[i] == toRemove) {
        array[i] = array[array.length - 1];
        array.pop();
        break;
      }
    }
  }

  function _hasActiveRole(string memory recordId, bytes32 userIdHash) internal view returns (bool) {
    bytes32 roleKey = _getRoleKey(recordId, userIdHash);
    return recordRoles[roleKey].isActive;
  }

  function _hasRole(
    string memory recordId,
    bytes32 userIdHash,
    string memory role
  ) internal view returns (bool) {
    bytes32 roleKey = _getRoleKey(recordId, userIdHash);
    RecordRole memory r = recordRoles[roleKey];
    return r.isActive && keccak256(bytes(r.role)) == keccak256(bytes(role));
  }

  function _isOwnerOrAdmin(
    string memory recordId,
    bytes32 userIdHash
  ) internal view returns (bool) {
    return
      _hasRole(recordId, userIdHash, 'owner') || _hasRole(recordId, userIdHash, 'administrator');
  }

  // =================== ROLE MANAGEMENT - EXTERNAL FUNCTIONS ===================

  /**
   * @notice Admin establishes first role (administrator or owner) on a new record
   * @param recordId The record ID
   * @param targetWallet A wallet address belonging to the user to assign the role to
   * @param role The role to assign ("administrator" or "owner")
   */
  function initializeRecordRole(
    string memory recordId,
    address targetWallet,
    string memory role
  ) external onlyAdmin {
    require(bytes(recordId).length > 0, 'Record ID cannot be empty');
    require(targetWallet != address(0), 'Invalid wallet address');
    require(_isValidRole(role), 'Invalid role string');

    // Get the identity for this wallet
    bytes32 targetIdHash = wallets[targetWallet].userIdHash;
    require(targetIdHash != bytes32(0), 'Wallet not registered');
    require(_isActiveMember(targetWallet), 'Must be an active member');

    require(
      ownersByRecord[recordId].length == 0 && adminsByRecord[recordId].length == 0,
      'Record already initialized'
    );

    _grantRoleInternal(recordId, targetIdHash, role, bytes32(0)); // bytes32(0) = admin
  }

  /**
   * @notice Grant a role to a user for a specific record
   * @param recordId The record ID
   * @param targetWallet A wallet address belonging to the target user
   * @param role The role to grant ("owner", "administrator", "viewer")
   */
  function grantRole(
    string memory recordId,
    address targetWallet,
    string memory role
  ) external onlyActiveMember {
    require(bytes(recordId).length > 0, 'Record ID cannot be empty');
    require(targetWallet != address(0), 'Wallet address cannot be zero');
    require(_isValidRole(role), 'Invalid role string');

    // Get identities
    bytes32 userIdHash = _getCallerIdHash();
    bytes32 targetIdHash = wallets[targetWallet].userIdHash;
    require(targetIdHash != bytes32(0), 'Target wallet not registered');

    bytes32 roleHash = keccak256(bytes(role));

    require(_hasActiveRole(recordId, userIdHash), 'You have no role for this record');

    bool ownerExists = ownersByRecord[recordId].length > 0;
    bool userIsOwner = _hasRole(recordId, userIdHash, 'owner');
    bool userIsAdmin = _hasRole(recordId, userIdHash, 'administrator');

    if (roleHash == keccak256(bytes('owner'))) {
      if (ownerExists) {
        require(userIsOwner, 'Only owners can grant owner role');
      } else {
        require(userIsAdmin, 'Only administrators can grant first owner');
      }
    } else if (roleHash == keccak256(bytes('administrator'))) {
      require(
        userIsOwner || userIsAdmin,
        'Only owners and administrators can grant administrator role'
      );
    } else if (roleHash == keccak256(bytes('viewer'))) {
      require(userIsOwner || userIsAdmin, 'Only owners and administrators can grant viewer role');
    }

    require(
      !_hasActiveRole(recordId, targetIdHash),
      'Target already has a role. Use changeRole() instead'
    );

    _grantRoleInternal(recordId, targetIdHash, role, userIdHash);
  }

  /**
   * @notice Change a user's existing role to a different role
   * @param recordId The record ID
   * @param targetWallet A wallet address belonging to the target user
   * @param newRole The new role ("owner", "administrator", "viewer")
   */
  function changeRole(
    string memory recordId,
    address targetWallet,
    string memory newRole
  ) external onlyActiveMember {
    require(bytes(recordId).length > 0, 'Record ID cannot be empty');
    require(targetWallet != address(0), 'Wallet address cannot be zero');
    require(_isValidRole(newRole), 'Invalid role string');

    bytes32 userIdHash = _getCallerIdHash();
    bytes32 targetIdHash = wallets[targetWallet].userIdHash;
    require(targetIdHash != bytes32(0), 'Target wallet not registered');

    bytes32 newRoleHash = keccak256(bytes(newRole));

    bytes32 targetRoleKey = _getRoleKey(recordId, targetIdHash);
    require(recordRoles[targetRoleKey].isActive, 'Target does not have an active role');

    string memory oldRole = recordRoles[targetRoleKey].role;
    bytes32 oldRoleHash = keccak256(bytes(oldRole));

    require(oldRoleHash != newRoleHash, 'Target already has this role');
    require(
      oldRoleHash != keccak256(bytes('owner')),
      'Owners cannot be demoted. Owner must voluntarily remove themselves.'
    );

    bool ownerExists = ownersByRecord[recordId].length > 0;
    bool userIsOwner = _hasRole(recordId, userIdHash, 'owner');
    bool userIsAdmin = _hasRole(recordId, userIdHash, 'administrator');
    bool userIsTarget = userIdHash == targetIdHash;

    if (newRoleHash == keccak256(bytes('owner'))) {
      if (ownerExists) {
        require(userIsOwner, 'Only owners can promote to owner');
      } else {
        require(userIsAdmin, 'Only administrators can promote first owner');
      }
    } else if (newRoleHash == keccak256(bytes('administrator'))) {
      require(
        userIsOwner || userIsAdmin,
        'Only owners and administrators can promote to administrator'
      );
    } else if (newRoleHash == keccak256(bytes('viewer'))) {
      if (ownerExists) {
        require(
          userIsOwner || (userIsAdmin && userIsTarget),
          'Only owners can demote others. Administrators can only demote themselves.'
        );
      } else {
        require(userIsAdmin, 'Only administrators can demote to viewer');
      }
    }

    if (
      oldRoleHash == keccak256(bytes('administrator')) && newRoleHash == keccak256(bytes('viewer'))
    ) {
      if (!ownerExists) {
        require(
          adminsByRecord[recordId].length > 1,
          'Cannot demote the last administrator when no owner exists'
        );
      }
    }

    _grantRoleInternal(recordId, targetIdHash, newRole, userIdHash);

    emit RoleChanged(recordId, targetIdHash, oldRole, newRole, userIdHash, block.timestamp);
  }

  /**
   * @notice Allows an owner to voluntarily give up their ownership
   * @param recordId The record ID
   */
  function voluntarilyLeaveOwnership(string memory recordId) external {
    require(bytes(recordId).length > 0, 'Record ID cannot be empty');

    bytes32 userIdHash = _getCallerIdHash();
    require(_hasRole(recordId, userIdHash, 'owner'), 'You are not an owner of this record');

    bool hasOtherOwners = ownersByRecord[recordId].length > 1;
    bool hasAdmins = adminsByRecord[recordId].length > 0;

    require(hasOtherOwners || hasAdmins, 'Cannot leave as last owner with no administrators');

    bytes32 roleKey = _getRoleKey(recordId, userIdHash);
    _removeFromRoleArray(recordId, userIdHash, 'owner');

    recordRoles[roleKey].isActive = false;

    emit OwnershipVoluntarilyLeft(recordId, userIdHash, block.timestamp);
  }

  /**
   * @notice Revoke a user's role entirely
   * @param recordId The record ID
   * @param targetWallet A wallet address belonging to the target user
   */
  function revokeRole(string memory recordId, address targetWallet) external {
    require(bytes(recordId).length > 0, 'Record ID cannot be empty');
    require(targetWallet != address(0), 'Wallet address cannot be zero');

    bytes32 userIdHash = _getCallerIdHash();
    bytes32 targetIdHash = wallets[targetWallet].userIdHash;
    require(targetIdHash != bytes32(0), 'Target wallet not registered');

    bytes32 targetRoleKey = _getRoleKey(recordId, targetIdHash);
    require(recordRoles[targetRoleKey].isActive, 'Target does not have an active role');

    string memory role = recordRoles[targetRoleKey].role;
    bytes32 roleHash = keccak256(bytes(role));

    require(
      roleHash != keccak256(bytes('owner')),
      'Owners cannot be revoked. Owner must use voluntarilyLeaveOwnership().'
    );

    bool isSelfRevoke = userIdHash == targetIdHash;

    if (!isSelfRevoke) {
      require(
        _isOwnerOrAdmin(recordId, userIdHash),
        'Only owners and administrators can revoke roles'
      );

      bool userIsOwner = _hasRole(recordId, userIdHash, 'owner');

      if (!userIsOwner) {
        bool targetIsAdmin = roleHash == keccak256(bytes('administrator'));

        if (targetIsAdmin) {
          bool ownerExists = ownersByRecord[recordId].length > 0;
          require(!ownerExists, 'Only owners can revoke administrators');
        }
      }
    }

    if (roleHash == keccak256(bytes('administrator'))) {
      bool hasOtherAdmins = adminsByRecord[recordId].length > 1;
      bool ownerExists = ownersByRecord[recordId].length > 0;

      require(
        hasOtherAdmins || ownerExists,
        'Cannot revoke last administrator without an owner present'
      );
    }

    _removeFromRoleArray(recordId, targetIdHash, role);

    recordRoles[targetRoleKey].isActive = false;

    emit RoleRevoked(recordId, targetIdHash, role, userIdHash, block.timestamp);
  }

  // =================== ROLE MANAGEMENT - VIEW FUNCTIONS ===================

  /**
   * @notice Check if wallet's identity has any active role on a record
   */
  function hasActiveRole(
    string memory recordId,
    address wallet
  ) external view override returns (bool) {
    bytes32 userIdHash = wallets[wallet].userIdHash;
    if (userIdHash == bytes32(0)) return false;
    return _hasActiveRole(recordId, userIdHash);
  }

  /**
   * @notice Check if wallet's identity has a specific role
   */
  function hasRole(
    string memory recordId,
    address wallet,
    string memory role
  ) external view override returns (bool) {
    bytes32 userIdHash = wallets[wallet].userIdHash;
    if (userIdHash == bytes32(0)) return false;
    return _hasRole(recordId, userIdHash, role);
  }

  /**
   * @notice Check if wallet's identity is owner or administrator
   */
  function isOwnerOrAdmin(
    string memory recordId,
    address wallet
  ) external view override returns (bool) {
    bytes32 userIdHash = wallets[wallet].userIdHash;
    if (userIdHash == bytes32(0)) return false;
    return _isOwnerOrAdmin(recordId, userIdHash);
  }

  /**
   * @notice Get full role details by wallet
   */
  function getRoleDetails(
    string memory recordId,
    address wallet
  ) external view returns (string memory role, bool isActive) {
    bytes32 userIdHash = wallets[wallet].userIdHash;
    if (userIdHash == bytes32(0)) {
      return ('', false);
    }
    bytes32 roleKey = _getRoleKey(recordId, userIdHash);
    RecordRole memory r = recordRoles[roleKey];
    return (r.role, r.isActive);
  }

  /**
   * @notice Get full role details by identity
   */
  function getRoleDetailsByUser(
    string memory recordId,
    bytes32 userIdHash
  ) external view returns (string memory role, bool isActive) {
    bytes32 roleKey = _getRoleKey(recordId, userIdHash);
    RecordRole memory r = recordRoles[roleKey];
    return (r.role, r.isActive);
  }

  /**
   * @notice Get all owner identities of a record
   */
  function getRecordOwners(string memory recordId) external view returns (bytes32[] memory) {
    return ownersByRecord[recordId];
  }

  /**
   * @notice Get all admin identities of a record
   */
  function getRecordAdmins(string memory recordId) external view returns (bytes32[] memory) {
    return adminsByRecord[recordId];
  }

  /**
   * @notice Get all viewer identities of a record
   */
  function getRecordViewers(string memory recordId) external view returns (bytes32[] memory) {
    return viewersByRecord[recordId];
  }

  /**
   * @notice Get all records where a user has any role
   */
  function getRecordsByUser(bytes32 userIdHash) external view returns (string[] memory) {
    return recordsByUser[userIdHash];
  }

  /**
   * @notice Get role statistics for a record
   */
  function getRecordRoleStats(
    string memory recordId
  ) external view returns (uint256 ownerCount, uint256 adminCount, uint256 viewerCount) {
    return (
      ownersByRecord[recordId].length,
      adminsByRecord[recordId].length,
      viewersByRecord[recordId].length
    );
  }

  /**
   * @notice Get total number of roles across all records
   */
  function getTotalRoles() external view returns (uint256) {
    return totalRoles;
  }
}

